import React, { Component } from 'react';
import {Item, Label, Input, Form} from  'native-base';
import PropTypes from 'prop-types';
import {View, Platform } from 'react-native';

export default class InputText extends Component {
    static get propTypes () {
        return{
            children: PropTypes.string.isRequired,
            onChangeText:PropTypes.func.isRequired,
            secureTextEntry:PropTypes.bool.isRequired,
            returnKeyType:PropTypes.string.isRequired,
            getRef:PropTypes.func,
            onSubmitEditing:PropTypes.func,
            autoCapitalize:PropTypes.string
        }
    }
    render(){
        //console.log("childeren:"+this.props.children)
        return(
            <Form>
                <Item {...this.props} floatingLabel>
                    <Label>{this.props.children}</Label>
                    <Input onChangeText={this.props.onChangeText} secureTextEntry={this.props.secureTextEntry}
                           returnKeyType = {this.props.returnKeyType}
                           style={this.props.style}
                           getRef={this.props.getRef} onSubmitEditing={this.props.onSubmitEditing}
                           value={this.props.value} autoCapitalize={this.props.autoCapitalize}/>
                </Item>
            </Form>
        )
    }
}

