import React, { Component } from 'react';
import {Input, InputGroup} from  'native-base';
import PropTypes from 'prop-types';
import {View, Platform } from 'react-native';

export default class InputText extends Component {
    static propTypes = {
        children: PropTypes.string.isRequired,
        onChangeText:PropTypes.func.isRequired,
        secureTextEntry:PropTypes.bool.isRequired
    }
    render(){
        console.log("childeren:"+this.props.children)
        return(
            <InputGroup>
                <Input onChangeText={this.props.onChangeText} secureTextEntry={this.props.secureTextEntry}
                       style={this.props.style} value={this.props.value} autoCapitalize="none"/>
            </InputGroup>
        )
    }
}

