import React, { Component } from 'react';
import { Content, Button, Text, View} from  'native-base';
import PropTypes from 'prop-types';


export default class CustomButtonNB extends Component {
    static propTypes = {
        children: PropTypes.string.isRequired,
        onPress: PropTypes.func.isRequired,
        style: PropTypes.objectOf.isRequired
    }

    render(){
       // console.log("childeren:"+this.props.children)
        return(
            <View>
                <Button onPress={this.props.onPress} rounded block style={this.props.style}>
                    <Text>{this.props.children}</Text>
                </Button>
            </View>
        )
    }
}

