import React, { Component } from 'react';
import InputText from './../../Components/InputText';
import PropTypes from 'prop-types';
import globalValues from './../../globalState'

export default class EMailNB extends Component {
    static get propTypes () {
        return{
            getRef:PropTypes.func,
            onSubmitEditing:PropTypes.func,
            returnKeyType:PropTypes.string.isRequired
        }
    }
    constructor(props){
        super(props);
        this.state={
            EMail:''
        }
    }
    changeEmailText(e){
        if(this.state.EMail == ''){
            var string1 = e.replace(/\s/g, '');
        }
        else{
            var string1 = e
        }
        this.setState({EMail:string1},()=>{
            var Email = this.state.EMail
            globalValues.Email = Email
        });
    }
    render() {
        console.log('this.props.ref')
        return(
            <InputText onChangeText={this.changeEmailText.bind(this)} secureTextEntry={false}
                       getRef={this.props.getRef} onSubmitEditing={this.props.onSubmitEditing} style={this.props.style}
                       returnKeyType = {this.props.returnKeyType} value={this.state.EMail} autoCapitalize={'None'}>Email</InputText>
        );
    }
}
