import React, { Component } from 'react';
import InputText from './../../Components/InputText';
import PropTypes from 'prop-types';
import globalValues from './../../globalState';


export default class FirstNameNB extends Component {
    static get propTypes () {
        return{
            getRef:PropTypes.func,
            onSubmitEditing:PropTypes.func,
            returnKeyType:PropTypes.string.isRequired
        }
    }
    constructor(props){
        super(props);
        this.state={
            FirstName:''
        }
    }
    changeFirstNameText(e){
        if(this.state.FirstName == ''){
            var string1 = e.replace(/\s/g, '');
        }
        else{
            var string1 = e
        }
        this.setState({FirstName:string1},()=>{
            var FirstName = this.state.FirstName
            globalValues.FirstName = FirstName
        });
    }
    render() {
        return(
            <InputText onChangeText={this.changeFirstNameText.bind(this)} secureTextEntry={false}
                       getRef={this.props.getRef} onSubmitEditing={this.props.onSubmitEditing}
                       style={this.props.style}
                       returnKeyType = {this.props.returnKeyType} value={this.state.FirstName}>First Name</InputText>
        );
    }
}
