import React, { Component } from 'react';
import InputText from './../../Components/InputText';
import PropTypes from 'prop-types';
import globalValues from './../../globalState';


export default class LastNameNB extends Component {
    static get propTypes () {
        return{
            getRef:PropTypes.func,
            onSubmitEditing:PropTypes.func,
            returnKeyType:PropTypes.string.isRequired
        }
    }
    constructor(props){
        super(props);
        this.state={
            LastName:''
        }
    }
    changeLastNameText(e){
        if(this.state.LastName == ''){
            var string1 = e.replace(/\s/g, '');
        }
        else{
            var string1 = e
        }
        this.setState({LastName:string1},()=>{
            var LastName = this.state.LastName
            globalValues.LastName = LastName
        });
    }
    render() {
        return(
            <InputText onChangeText={this.changeLastNameText.bind(this)} secureTextEntry={false}
                       getRef={this.props.getRef} onSubmitEditing={this.props.onSubmitEditing}
                       style={this.props.style}
                       returnKeyType = {this.props.returnKeyType} value={this.state.LastName}>Last Name</InputText>
        );
    }
}
