import React, { Component } from 'react';
import InputText from './../../Components/InputText';
import PropTypes from 'prop-types';
import globalValues from './../../globalState'


export default class PasswordNB extends Component {
    static propTypes = {
        getRef:PropTypes.func,
        onSubmitEditing:PropTypes.func,
        returnKeyType:PropTypes.string.isRequired
    }
    constructor(props){
        super(props);
        this.state={
            Password:''
        }
    }
    changePasswordText(e){
        if(this.state.Password == ''){
            var string1 = e.replace(/\s/g, '');
        }
        else{
            var string1 = e
        }
        this.setState({Password:string1},()=>{
            var Password = this.state.Password
            globalValues.Password = Password
        });
    }
    render() {
        return(
            <InputText onChangeText={this.changePasswordText.bind(this)} secureTextEntry={true}
                       getRef={this.props.getRef}  onSubmitEditing={this.props.onSubmitEditing}
                       style={this.props.style}
                       returnKeyType = {this.props.returnKeyType} value={this.state.Password} autoCapitalize={'None'}>Password</InputText>
        );
    }
}
