import React, { Component } from 'react';
import InputText from './../../Components/InputText';
import PropTypes from 'prop-types';
import globalValues from './../../globalState';


export default class PhoneNumberNB extends Component {
    static propTypes = {
        getRef:PropTypes.func,
        onSubmitEditing:PropTypes.func,
        returnKeyType:PropTypes.string.isRequired
    }
    constructor(props){
        super(props);
        this.state={
            PhoneNumber:''
        }
    }
    changePhoneNumberText(e){
        if(this.state.PhoneNumber == ''){
            var string1 = e.replace(/\s/g, '');
        }
        else{
            var string1 = e
        }
        this.setState({PhoneNumber:string1},()=>{
            var PhoneNumber = this.state.PhoneNumber
            globalValues.PhoneNumber = PhoneNumber
        });
    }
    render() {
        return(
            <InputText onChangeText={this.changePhoneNumberText.bind(this)} secureTextEntry={false}
                       getRef={this.props.getRef} onSubmitEditing={this.props.onSubmitEditing}
                       style={this.props.style}
                       returnKeyType = {this.props.returnKeyType} value={this.state.PhoneNumber}>Phone Number</InputText>
        );
    }
}
