import React, { Component } from 'react';
import {Container, Header, Content, Item, Input, Label, Button, Text} from 'native-base';
import InputText from './../../Components/InputText';
import PropTypes from 'prop-types';
import globalValues from './../../globalState';

export default class UserNameNB extends Component {
    static propTypes = {
        getRef:PropTypes.func,
        onSubmitEditing:PropTypes.func,
        returnKeyType:PropTypes.string.isRequired
    }
    constructor(props){
        super(props);
        this.state={
            UserName:''
        }
    }
    changeUserNameText(e){
        if(this.state.UserName == ''){
            var string1 = e.replace(/\s/g, '');
        }
        else{
            var string1 = e
        }
        this.setState({UserName:string1},()=>{
            var UserName = this.state.UserName
            globalValues.UserName = UserName
        });
    }
    render() {
        return(
            <InputText onChangeText={this.changeUserNameText.bind(this)} secureTextEntry={false}
                       getRef={this.props.getRef} onSubmitEditing={this.props.onSubmitEditing}
                       style={this.props.style}
                       returnKeyType = {this.props.returnKeyType} value={this.state.UserName}>USER NAME</InputText>
        );
    }
}
